var re = /([0-9]{2})%/
var idx = msg.payload.search(re);
var str = msg.payload;
if (idx >=0) {
    str = msg.payload.substring(idx, idx + 2);
}
msg.payload = str;
return msg;

const tokens = msg.payload.split(/(\s+)/).filter((token) => token.trim().length > 0);
const used = parseInt(tokens[2], 10);
const total = parseInt(tokens[3], 10) || 1;
const percent = Math.round(used / total *1000.0)/10;
msg.payload = percent;
return msg;

