const pad = (number, length) => {
  return String(number).padStart(length, '0');
};

const date = new Date();
const year = date.getFullYear();
const month = pad(date.getMonth() + 1, 2); // Months in JavaScript are zero-indexed
const day = pad(date.getDate(), 2);

const datestamp = `${year}-${month}-${day}`;

const filename = `audio_log_${datestamp}.csv`;
msg.filename = `/home/pi/audio_files/${filename}`;
msg.content = msg.payload;
msg.payload = {pattern: filename};

return msg;

const loudness = context.get("loudness") || 0;
const peak = context.get('peak') || 0;
if (msg.payload.loudness !== loudness || msg.payload.peak !== peak) {
    context.set('loudness', msg.payload.loudness);
    context.set('peak', msg.payload.peak);
    return msg;
}
return null;

const now = new Date();
const seconds = now.getSeconds();
if (seconds === 0) {
    const hours = now.getHours().toString().padStart(2,'0');
    const minutes = now.getMinutes().toString().padStart(2,'0');
    const offset = now.getTimezoneOffset();
    const isDST = offset === -60;
    const timeZone = isDST ? "BST" : "GMT";
    msg.payload = `${hours}:${minutes} ${timeZone}`;
    return msg;
}
return null;

const rate = Number(msg.payload);
const kHz = Math.round(rate / 100) / 10;

return { payload: `${kHz}kHz`};

